import React, { Component } from "react";
import {
  Col,
  Row,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  Container
} from "reactstrap";

export default class FormComponent extends Component {
  state = {
    nama: "",
    alamat: "",
    status: 0
  }

  constructor(){
    super();
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange = function(event){
    this.setState({nama:event.value});
    console.log(event.value);
  }

  render() {
    return (
      <Form>
        <Row>
          <Col sm={6} lg={6} xl={6}>
            <FormGroup row>
              <Label for="nama" sm={2}>
                Nama
              </Label>
              <Col sm={10}>
                <Input
                  type="text"
                  name="nama"
                  placeholder="Nama Penuh"
                  onChange={this.handleChange}
                />
              </Col>
            </FormGroup>
          </Col>
          <Col sm={6} lg={6} xl={6}>
            <FormGroup row>
              <Label for="alamat" sm={2}>
                Alamat
              </Label>
              <Col sm={10}>
                <Input
                  type="text"
                  name="alamat"
                  placeholder="Alamat"
                />
              </Col>
            </FormGroup>
          </Col>
          <Col sm={6} lg={6} xl={6}>
            <FormGroup check>
              <Label check>
                <Input type="checkbox" id="checkbox2" value="1" />{' '}
                Status
              </Label>
            </FormGroup>
          </Col>
        </Row>
        <Row>
          <Col className="text-right">
            <Button outline color="primary">Simpan</Button>{' '}
          </Col>
        </Row>
      </Form>
    );
  }
}
