import React, { Component } from "react";
import {
  Col,
  Row,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  Container,
} from "reactstrap";

export default class ListComponent extends Component {
  state = {
    data: [],
    listData:"" 
  }

  constructor(){
    super();

    this.loadData();
  }

  loadData = function(){
    var st = this;

    const requestOptions = {
      method: 'GET',
      headers: { 'Content-Type': 'application/json' },
    };
    fetch('http://localhost:8000/api/people/', requestOptions)
      .then(response => response.json())
      .then((data) => this.listPeople(data));
      // .then(data => this.setState({ data: data }));
  }

  listPeople = function(data){
    let tmp = "";
    data.forEach((v,k) => {
      tmp += '<li>'+v.nama+'</li>';
    });
    st.setState({ listData: tmp, data: data});
    return tmp;
  }

  render() {
    return (
      <Row>
        <Col>
          <ul>
            {this.state.listData}
          </ul>
        </Col>
      </Row>
    );
  }
}
