import React, { Component } from "react";
import {
  Col,
  Row,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  Container,
} from "reactstrap";

export default class ListComponent extends Component {
  state = {
    data: [],
    loading: true
  }

  constructor(){
    super();

    this.loadData();
  }

  loadData = function(){
    let st = this;

    this.setState({loading: true});
    const requestOptions = {
      method: 'GET',
      headers: { 'Content-Type': 'application/json' },
    };
    fetch('http://localhost:8000/api/people/', requestOptions)
      .then(response => response.json())
      .then((data) =>{
        st.setState({data: data, loading: false});
      });
      // .then(data => this.setState({ data: data }));
  }

  renderList = function(){
    let tmp = '';
    this.state.data.forEach((v,k) => {
      tmp += '<li>';
      tmp += v.nama;
      tmp += '</li>';
    });
    return tmp;
  }

  render() {
    return (
      <Row>
        <Col>
          <ul>
            {!this.state.loading?this.renderList:'<li>Loading...<li/>'}
          </ul>
        </Col>
      </Row>
    );
  }
}
